package homework;

public class circle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double PI = 3.1415;
		int line = 5;
		double circle = line * 2 * PI;
		double circle_2 = line * line * PI;
		System.out.println("這個圓的圓周長為" + circle + "公分");
		System.out.println("這個圓的圓面積為" + circle_2 + "平方公分");

	}

}
